sys_script_client.script:
function onLoad() {


	var URLdata = [];
	var TitleData= [];
	var gr = new GlideRecord('sp_portal');

	gr.addNotNullQuery('url_suffix');
	gr.query();

	while( gr.next() ){
		URLdata.push(gr.getValue('url_suffix') );
		TitleData.push(gr.getValue('title') );
	}

var PortalData = {};
	for (var i=0; i < URLdata.length; i++){
		PortalData[TitleData[i]] = URLdata[i];
	}

PortalData = JSON.stringify(PortalData);	
}
