sysauto_script.script:
//Retrieve the value of the `autoCloseOverdue` application property.
var howLongToClose = gs.getProperty('x_58872_needit.autoCloseOverdue');

//Query the database for the NeedIt Task records with Due Date  
// values older than the amount of days set in `autoCloseOverdue`
//Only return NeedIt Task records with a STATE value of
// 'open' or 'work in progress' then close the NEedIt Tasks
// and update the Task Work notes.

var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
overdueNITasks.addQuery('due_date','<=',gs.daysAgo((howLongToClose)) );
overdueNITasks.addQuery('state','<',3);

overdueNITasks.query();

//Write a log message for each overdue task record.
//Add a note to the Work Notes field. Change the state to Closed Incomplete.
// Update the NeedIt Task Record.

while(overdueNITasks.next() ){
	gs.info("Auto close NeedIt Task Record: " + overdueNITasks.number);
	overdueNITasks.work_notes = "Record closed because it has been far past the due date.";
	overdueNITasks.state=4;
	overdueNITasks.update();
}
